import React from 'react'
import styled from 'styled-components/macro'

export const HeaderTitle = styled.p`
  font-size: 22px;
  font-weight: bolder;
  letter-spacing: 1.12px;
  color: #ffa931;
  opacity: 1;
  letter-spacing: 0.52px;
  margin-bottom: 0;
  @media (min-width: 668px) and (max-width: 1023px) {
    font-size: 30px;
  }
  @media (min-width: 1024px) {
    font-size: 28px;
    padding-top: 0;
  }
`
export const HeaderBigTitle = styled.h1`
  font-weight: bold;
  font-size: 40px;
  color: #ffa931;
  margin: 0px;
  @media (min-width: 668px) and (max-width: 1023px) {
    font-size: 50px;
  }
  @media (min-width: 1024px) {
    font-size: 82px;
    color: #FFFFFF;
  }
`
export const HeaderSubtext = styled.h2`
  font-weight: bold;
  font-size: 24px;
  color: #ffa931;
  margin: 0px;
  @media (min-width: 668px) and (max-width: 1023px) {
    display: flex;
    font-size: 30px;
  }
  @media (min-width: 1024px) {
    font-size: 50px;
    color: #FFFFFF;
  }
`
export const FirstBoxText = styled.p`
  font-family: 'Montserrat';
  font-size: 17px;
  text-align: center;
  letter-spacing: 0;
  color: #000000;
  margin: 1.6em 1.3em 0em 1.3em;
  @media (min-width: 1024px) {
    font-size: 30px;
    text-align: left;
    margin: 4em 2.3em 0.5em 2.3em;
    line-height: 1.2;
  }
`
export const BoxText = styled.p`
  font-family: 'Montserrat';
  font-size: 17px;
  text-align: center;
  letter-spacing: 0;
  color: #000000;
  margin: 1.6em 1.3em 0em 1.3em;
  @media (min-width: 1024px) {
    font-size: 30px;
    text-align: left;
    margin: 0em 2.3em 0.5em 2.3em;
    line-height: 1.2;
  }
`
export const TechBoxText = styled.p`
  font-family: 'Montserrat';
  font-size: 17px;
  text-align: center;
  letter-spacing: 0;
  color: #000000;
  margin: 1.6em 1.3em 0em 1.3em;
  @media (min-width: 1024px) {
    font-size: 30px;
    margin: 0em 2.3em 0.5em 2.3em;
    line-height: 1.2;
  }
`
export const BoxHeader = styled.h3`
  font-family: 'Montserrat';
  font-size: 21px;
  color: #FFFFFF;
  font-weight: bolder;
  margin: 0px;
  padding: 2px 5px;
  @media (min-width: 1024px) {
    font-size: 48px;
  }
`
export const FeatureRedTitle = styled.p`
  font-family: 'Roboto';
  font-size: 17px;
  font-weight: bold;
  color: #ffa931;
  text-align: left;
  margin: 0;
  @media (min-width: 1024px) {
    font-size: 25px;
  }
`
export const FeatureText = styled.p`
  font-family: 'Roboto';
  font-size: 17px;
  color: #000000;
  text-align: left;
  margin: 0;
  @media (min-width: 1024px) {
    font-size: 25px;
  }
`
export const ThoughtTitle = styled.p`
  font-family: 'Roboto';
  font-size: 17px;
  font-weight: 600;
  color: #000000;
  text-align: left;
  margin: 0;
  @media (min-width: 1024px) {
    font-size: 25px;
  }
`
export const ThoughtText = styled.p`
  font-family: 'Roboto';
  font-size: 17px;
  color: #000000;
  text-align: left;
  margin: 0;
  @media (min-width: 1024px) {
    font-size: 21px;
  }
`
export const TagText = styled.p`
  font-family: 'Roboto';
  font-size: 9px;
  color: #dcedc2;
  text-align: center;
  margin: 0;
  @media (min-width: 1024px) {
    font-size: 16px;
    text-align: left;
  }
`
export const RedHeader = styled.h4`
  display: flex;
  align-self: center;
  font-family: 'Montserrat';
  font-size: 21px;
  color: #ffa931;
  font-weight: bolder;
  margin-top: 30px;
  margin-bottom: 30px;
  @media (min-width: 1024px) {
    font-size: 38px;
    margin-top: 100px;
    margin-bottom: 60px;
  }
`
export const SkillsTheme = styled.p`
  font-family: 'Montserrat';
  font-size: 17px;
  text-align: center;
  letter-spacing: 0;
  font-weight: bold;
  color: #ffa931;
  margin: 1.6em 1.3em 0em 1.3em;
  @media (min-width: 1024px) {
    font-size: 30px;
    margin: 0em 0.5em 0.5em 0.5em;
    line-height: 1.2;
  }
`
export const SkillsText = styled.p`
  font-family: 'Montserrat';
  font-size: 17px;
  text-align: center;
  letter-spacing: 0;
  color: #000000;
  margin: 0.5em 0em 0em 0em;
  @media (min-width: 1024px) {
    font-size: 30px;
    margin: 0em 1.3em 0.2em 1.3em;
    line-height: 1.2;
  }
`
export const FooterHeader = styled.h3`
  font-family: 'Montserrat';
  font-size: 21px;
  color: #ffa931;
  font-weight: bolder;
  margin-top: 2em;
  @media (min-width: 1024px) { 
    font-size: 48px;
  }
`
export const ColorIconText = styled.p`
  display: flex;
  font-family: 'Montserrat';
  font-size: 11px;
  color: #ffa931;
  margin-top: 1em;
  @media (min-width: 668px) and (max-width: 1023px) {
    margin: 1em 1em 0em 0em;
  }
  @media (min-width: 1024px) { 
    font-size: 25px;
    margin-top: 10px;
  }
`
export const FooterText = styled.p`
  font-family: 'Montserrat';
  font-size: 17px;
  text-align: center;
  letter-spacing: 0;
  color: #ffa931;
  margin: 1.6em 1.3em 0em 1.3em;
  @media (min-width: 1024px) {
    font-size: 30px;
    margin-bottom: 3em;
    line-height: 1.2;
  }
`
